package com.astrology.zodiacs.di.componentimport android.app.Applicationimport com.astrology.zodiacs.MyApplicationimport com.astrology.zodiacs.di.modules.AppModuleimport com.astrology.zodiacs.di.ActivityBuilderimport com.astrology.zodiacs.di.modules.NetworkModuleimport dagger.BindsInstanceimport dagger.Componentimport dagger.android.AndroidInjectionModuleimport dagger.android.AndroidInjectorimport dagger.android.DaggerApplicationimport javax.inject.Singleton@Singleton@Component(    modules = [        AppModule::class,        ActivityBuilder::class,        NetworkModule::class,        AndroidInjectionModule::class])interface AppComponent : AndroidInjector<DaggerApplication> {    fun inject(myApp: MyApplication)//    val sharedPref: SharedPreferences    @Component.Builder    interface Builder {        @BindsInstance        fun application(application: Application): Builder        fun build(): AppComponent    }}